const buyLinks=document.querySelectorAll(".card__button-to-cart"),modalCart=document.querySelector(".modal-cart"),addProduct=document.querySelector(".select-form__button");Array.from(buyLinks).forEach((e=>{e.addEventListener("click",(e=>{e.preventDefault(),modalCart.classList.add("modal-cart--opened")}))})),addProduct.addEventListener("click",(()=>{modalCart.classList.remove("modal-cart--opened")})),window.addEventListener("keydown",(e=>{"Escape"===e.key&&modalCart.classList.contains("modal-cart--opened")&&(e.preventDefault(),modalCart.classList.remove("modal-cart--opened"))}));